package vip.yeee.memo.integrate.kafka.handler;import lombok.extern.slf4j.Slf4j;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.kafka.listener.ConsumerAwareListenerErrorHandler;/** * */@Configuration@Slf4jpublic class ConsumerListenerErrorHandler {    @Bean    public ConsumerAwareListenerErrorHandler consumerAwareErrorHandler() {        return (message, exception, consumer) -> {            log.error(">>> 消费异常 {}",message.getPayload());            message.getPayload();            consumer.commitSync();            return null;        };    }}